################################################################################

### OPENBOR

if [ "$SYSTEM" == 'openbor' ]; then
    exec /opt/OpenBOR/OpenBOR.sh "$CORE" "$ROM"
fi

################################################################################

### GAMECUBE

if [ "$SYSTEM" == 'gamecube' ]; then
    mouse-pointer on

    ### FULLBOOT E FASTBOOT
    BIOS="$HOME/configs/dolphin-emu/Dolphin.ini"
    CONFIG="$HOME/batocera.conf"	
    BIOS_DIR="$HOME/../bios/GC"
    SAVE_DIR="$HOME/../saves/dolphin-emu/GC"

    for DIR in EUR USA JAP; do
        if ! [ -L "${SAVE_DIR}/${DIR}/IPL.bin" ]; then
            mkdir -p "${SAVE_DIR}/${DIR}"
            ln -s "${BIOS_DIR}/${DIR}/IPL.bin"  "${SAVE_DIR}/${DIR}/IPL.bin"
        fi
        ### FULLBOOT (Sempre Padrão)
        if [ "$(grep '^gamecube.fullboot=0' "$CONFIG")" ]; then
            sed -i 's/^SkipIPL.*/SkipIPL = True/' "$BIOS"
        elif [ "$(grep '^#gamecube.fullboot=0' "$CONFIG")" ]; then
            sed -i 's/^SkipIPL.*/SkipIPL = False/' "$BIOS"
        fi
    done
fi

################################################################################

### NINTENDO WII

if [ "$SYSTEM" == 'wii' ]; then
    mouse-pointer on

    ### FIX ASSINATURA DIGITAL (Sempre desativado)
    SIG="$HOME/configs/dolphin-emu/Dolphin.ini"
    if [ "$(grep '^EnableSignatureChecks = True' "$SIG")" ]; then
        sed -i 's/^EnableSignatureChecks.*/EnableSignatureChecks = False/' "$SIG"
    fi
fi

################################################################################

### LINUX

if [ "$SYSTEM" == 'linux' ]; then
    # Carrega o jogo de uma pasta.
    if [ -d "$ROM" ]; then
        cd "$ROM"
        if ! [ -x ./batocera.plus ]; then
            chmod +x ./batocera.plus
        fi
        ./batocera.plus $@;
    elif [ -f "${ROM%plus}plus" ] || [ -f "${ROM%PLUS}PLUS" ]; then
        # Carrega o jogo compactado no formato squashfs.
        if mount "$ROM" $HOME/../extractions; then
            cd "$HOME/../extractions"
            ./batocera.plus $@;
            cd ..
            sleep 0.5
            umount -f "$HOME/../extractions"
        fi
    fi
    exit $?
fi

################################################################################

### WINDOWS

if [ "$SYSTEM" == 'windows' ]; then

    ### Captura o nome da rom sem extenção ###

    ROM_NAME="$(basename "$ROM")"

    if [ "$ROM_NAME" == "${ROM_NAME%plus}plus" ]; then
        ROM_NAME="${ROM_NAME%.plus}"
    elif [ "$ROM_NAME" == "${ROM_NAME%PLUS}PLUS" ]; then
        ROM_NAME="${ROM_NAME%.PLUS}"
    else
        exit 1
    fi

    ### Monta e acessa o diretório do jogo ###

    if [ -d "$ROM" ]; then
        cd "$ROM"
    elif [ -f "$ROM" ]; then
        MOUNT_DIR="$HOME/../extractions/$(basename "$ROM")"

        mkdir -p "$MOUNT_DIR"

        if mount "$ROM" "$MOUNT_DIR"; then
            cd "$MOUNT_DIR"
            ROM="$MOUNT_DIR"
        else
            exit $?
        fi
    else
        exit 1
    fi

    ### Configura o save ###

    WIN_SAVE_DIR="$(cat batocera.plus | grep '^WIN_SAVE_DIR=' | cut -d "'" -f 2)"

    if [ "$WIN_SAVE_DIR" ]; then

        ### Cria o diretório de save (batocera e wine) ###

        BATOCERA_SAVE="$HOME/../saves/windows/${ROM_NAME}"

        if ! [ -d "${BATOCERA_SAVE}" ]; then
            mkdir -p "${BATOCERA_SAVE}"
        fi

        WIN_SAVE_DIR="$HOME/configs/wine/drive_c/${WIN_SAVE_DIR:3}"

        if ! [ -e "${WIN_SAVE_DIR}" ]; then
            mkdir -p "${WIN_SAVE_DIR}/.."
            rm -r  "${WIN_SAVE_DIR}"
            ln -sf "${BATOCERA_SAVE}" "${WIN_SAVE_DIR}"
        fi
    fi

    ### Executa o jogo ###

    RES_START="$(batocera-resolution currentMode)"

    if [ "$ROM" == "$MOUNT_DIR" ]; then
        ./batocera.plus $@
        cd ..
        umount -f "$MOUNT_DIR"
        rmdir "$MOUNT_DIR"
    else
        if ! [ -x ./batocera.plus ]; then
            chmod +x ./batocera.plus
        fi
        ./batocera.plus $@
    fi

    RES_STOP="$(batocera-resolution currentResolution)"

    if [ "$RES_START" != "$RES_STOP" ]; then
        batocera-resolution setMode "$RES_START"
    fi

    exit 0
fi

